package sec03;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		// result = result + 10
		result += 10;
		System.out.println("result=" + result);
		
		// result = result - 5
		result -= 5;
		System.out.println("result=" + result);
		
		// result = result * 3
		result *= 3;
		System.out.println("result=" + result);
		
		// result = result / 5
		result /= 5;
		System.out.println("result=" + result);
		
		// result = result % 3
		result %= 3;
		System.out.println("result=" + result);
	}

}
